mapper.select:
SELECT ii.car_mark carMark, ecb.car_brand_name carBrandName, eem.contact_mobile contactMobile, eem.ent_member_name entMemberName, eem.address, FROM_UNIXTIME(ii.arrive_time) arriveTime, ii.vin, ii.is_invoice invoice, ii.car_no carNo, ii.rear_img rearImg, ii.front_img frontImg, ii.drive_license driveLicense FROM ibs_insurance ii LEFT JOIN es_car_brand ecb ON ii.car_brand_id=ecb.car_brand_id LEFT JOIN es_ent_member eem ON ii.repair_id=eem.member_id WHERE ii.id=#{id}
AND ii.repair_id=#{repairId}
AND ii.flag=1

SELECT REPLACE ( ecb.brand_logo, #{param2}, #{param1} ) AS brandLogo, ii.car_mark carMark, ii.id, irpe.is_read isRead, DATE_FORMAT(irpe.create_date,'%Y-%m-%d' ) askTimeStr, ( SELECT COUNT(*) FROM ibs_report_price irp WHERE irp.ins_id = ii.id AND irp.member_id = #{supplierMemberId} ) partCount, (SELECT iii.`name` FROM ibs_insurance_info iii INNER JOIN ibs_report_price irp ON iii.id = irp.ins_info_id WHERE irp.ins_id = ii.id AND irp.member_id = #{supplierMemberId} AND irp.state = '0' limit 0,1) partName FROM ibs_insurance ii LEFT JOIN ibs_report_price_extend irpe ON irpe.ins_id = ii.id LEFT JOIN es_car_brand ecb ON ii.car_brand_id = ecb.car_brand_id WHERE irpe.supplier_member_id = #{supplierMemberId}
AND irpe.report_state=#{reportState}
AND irpe.quote_deadline < NOW()
AND irpe.quote_deadline > NOW()
AND ii.report_state IN
#{item}
ORDER BY irpe.id DESC

SELECT COUNT(*) FROM ibs_insurance ii LEFT JOIN ibs_report_price_extend irpe ON irpe.ins_id = ii.id WHERE irpe.supplier_member_id = #{supplierMemberId}
AND irpe.report_state=#{reportState}
AND irpe.quote_deadline < NOW()
AND irpe.quote_deadline > NOW()
AND irpe.is_read=#{isRead}
AND ii.report_state IN
#{item}

mapper.update:
 		UPDATE 
 			ibs_insurance


 				report_state = #{reportState}


		 WHERE 
		 	id = #{id}

		 	AND
		 		repair_id=#{repairId}
